mapper.select:
SELECT no, id, pwd, profile, name, nickname, birthdate, phone, zipcode, address, detailadd, regdate, usertype
FROM users
WHERE no = #{no}

mapper.update:
UPDATE users
SET pwd = #{pwd}, profile = #{profile}, nickname = #{nickname}, phone= #{phone}, 
zipcode = #{zipcode}, address = #{address}, detailAdd = #{detailAdd}
WHERE no = #{no}

mapper.select:
SELECT no, id, pwd, profile, name, nickname, birthdate, phone, zipcode, address, detailadd, regdate, usertype
FROM users
WHERE id = #{id}
AND pwd = #{pwd}
